#ifndef TINYSF_H
#define TINYSF_H

#include "common_structs.h"

/* Public Types for Use */
typedef unsigned int tinysf_s;

/* We have no doubles or floats in this system */
#define double long // Use a signed 64-bit integer type.
#define float int // Use a signed 32-bit integer type.

/* Public TINYSF Library Functions */
tinysf_s toTinySF(Number_s *num);
int toNumber(Number_s *num, tinysf_s value);
tinysf_s negateTinySF(tinysf_s value);
tinysf_s opTinySF(char operator, tinysf_s val1, tinysf_s val2);

// Floating Point Precision
#define TINYSF_SPECIAL      1
#define TINYSF_SIGNED       1
#define TINYSF_DENORMALIZED 0
#define TINYSF_ROUND_EVEN   1
#define TINYSF_SIGN_BITS    1
#define TINYSF_EXP_BITS     4
#define TINYSF_FRAC_BITS    8
#define TINYSF_TOTAL_BITS   TINYSF_SIGN_BITS + TINYSF_EXP_BITS + TINYSF_FRAC_BITS

#endif
